import React from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import {FormErrors} from './Formerrors.js'
export class Login extends React.Component { 
   constructor (props) {
  super(props);
  this.state = {
    email: '',
    password: '',
    formErrors: {email: '', password: ''},
    emailValid: false,
    passwordValid: false,
    formValid: false
  }
}
validateField(fieldName, value) {
  let fieldValidationErrors = this.state.formErrors;
  let emailValid = this.state.emailValid;
  let passwordValid = this.state.passwordValid;

  switch(fieldName) {
    case 'email':
      emailValid = value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i);
      fieldValidationErrors.email = emailValid ? '' : ' is invalid';
      break;
    case 'password':
      passwordValid = value.length >= 6;
      fieldValidationErrors.password = passwordValid ? '': ' is too short';
      break;
    default:
      break;
  }
  this.setState({formErrors: fieldValidationErrors,
                  emailValid: emailValid,
                  passwordValid: passwordValid
                }, this.validateForm);
}

validateForm() {
  this.setState({formValid: this.state.emailValid && this.state.passwordValid});
}
handleUserInput (e) {
  const name = e.target.name;
  const value = e.target.value;
  this.setState({[name]: value});
}

    render (){
        return (
            <div>
                <div className="rounded-left example z-depth-5 #c5cae9 indigo lighten-4jn" alt="100x100">    
                    <div className="border border-light p-5">
                        <div className="card-body">
                            <h2 className="font-weight-bold deep-orange-lighter-hover mb-3 .bounceIn">Inicio de sesión</h2>
                            <p className="card-text">
                            <div className="align-self-baseline">
                                <div className="form-group">
                                    <input type="email" id="defaultLoginFormEmail" className="form-control mb-4" placeholder="E-mail" value={this.state.email} onChange={(event) => this.handleUserInput(event)} />
                                    <input type="password" id="defaultLoginFormPassword" className="form-control mb-4" placeholder="Password" value={this.state.password} onChange = {(event) => this.handleUserInput(event)} />
                                    <label htmlFor="inputValidationEx" onChange={(event) => this.handleUserInput(event)} data-error="wrong"data-success="right"> <a href="google.com">Olvidaste la contraseña?</a></label>
                                    </div>
                            </div>
                            </p>
                            <a href="#!" className="btn btn-primary">Ingresar</a>
                        </div>
                    </div>
                </div>   
                <div className="panel panel-default">
                    <FormErrors formErrors={this.state.formErrors} />
                </div>
            </div>
            );
        };
    };

export default Login;
